CommandTexts.CommandText:

IF ((SELECT count(*) from FN_LISTEXTENDEDPROPERTY('MS_Description',N'SCHEMA',@schema,@type,@table,NULL,DEFAULT))>0)
  EXEC sp_updateextendedproperty
    @name = N'MS_Description',
    @value = @comments,
    @level0type = 'SCHEMA',
    @level0name = @schema,
    @level1type = @type,
    @level1name = @table
ELSE
  EXEC sp_addextendedproperty
    @name = N'MS_Description',
    @value = @comments,
    @level0type = N'SCHEMA',
    @level0name = @schema,
    @level1type = @type,
    @level1name = @table,
    @level2type = NULL,
    @level2name = NULL
