CommandTexts.CommandText:

IF ((SELECT COUNT(*) from fn_listextendedproperty('MS_Description','SCHEMA', @schema,@type, @table,'COLUMN', @column)) > 0) 
  EXEC sp_updateextendedproperty
    @name = N'MS_Description',
    @value = @comments,
    @level0type = 'SCHEMA',
    @level0name = @schema,
    @level1type = @type,
    @level1name = @table,
    @level2type = 'COLUMN',
    @level2name = @column
ELSE
  EXEC sp_addextendedproperty
    @name = N'MS_Description',
    @value = @comments,
    @level0type = 'SCHEMA',
    @level0name = @schema,
    @level1type = @type,
    @level1name = @table,
    @level2type = 'COLUMN',
    @level2name = @column
