statements.insert:
BEGIN BEGIN TRAN;
BEGIN TRY DELETE FROM [Dynamic].[ExtensionPropertyInstance] WHERE [EntityId]=#EntityId#;
INSERT INTO [Dynamic].[ExtensionPropertyInstance] ( [Id], [ExtensionPropertyId], [EntityId], [Value] ) VALUES ( NEWID(), #Instances[].ExtensionPropertyId#, #EntityId#, #Instances[].Value# )
COMMIT TRAN; END TRY BEGIN CATCH ROLLBACK TRAN; END CATCH END
