statements.insert:
DELETE FROM RBAC.StaffOrganize WHERE UserId=#UserId#;
INSERT INTO RBAC.StaffOrganize ( Id, UserId, OrganizationId, CreateUserId, CreateDateTime ) VALUES
( NEWID(), #UserId#, #OrganizationIds[]#, #CreateUserId#, GETDATE() )

DELETE FROM RBAC.UserRole WHERE UserId=#UserId#;
INSERT INTO RBAC.UserRole ( Id, UserId, RoleId, CreateUserId, CreateDateTime ) VALUES
( NEWID(), #UserId#, #RoleIds[]#, #CreateUserId#, GETDATE() )
